<div class="page-header">	<h1><?php echo lang('daftar:data:plural'); ?></h1>	<?php file_partial('shortcuts'); ?></div><style type="text/css" media="screen">	.chzn-container.chzn-container-single {		margin-right: 15px;	}</style><?php echo form_open('', array('class' => 'form-inline', 'method' => 'get', 'id'=>'filterform'), array('f_module' => $module_details['slug'])) ?>	<div class="form-group">		<label style="margin-right: 5px;"><?php echo lang('daftar:nama'); ?>:&nbsp;</label>		<input type="text" name="f-nama" value="<?php echo $this->input->get('f-nama'); ?>">		<label style="margin-right: 5px; margin-left: 10px;"><?php echo lang('daftar:status'); ?>:&nbsp;</label>		<?php echo form_dropdown('f-status', $status, $this->input->get('f-status')); ?>		<button href="<?php echo current_url() . '#'; ?>" class="add" type="submit">			<i class="fa fa-search"></i>			<?php echo lang('global:filters'); ?>		</button>		<button href="<?php echo current_url() . '#'; ?>" class="add" type="reset">			<i class="fa fa-times"></i>			<?php echo lang('buttons:clear'); ?>		</button>	</div><?php echo form_close() ?><?php if ($data['total'] > 0): ?>	<p class="pull-right"><?php echo lang('daftar:showing').' '.count($data['entries']).' '.lang('daftar:of').' '.$data['total'] ?></p>	<table class="table table-striped table-bordered table-hover">		<thead>			<tr>				<th>No</th>				<th><?php echo lang('daftar:nama'); ?></th>				<th><?php echo lang('daftar:status'); ?></th>				<th>Data</th>				<th><?php echo lang('daftar:created'); ?></th>				<th></th>			</tr>		</thead>		<tbody>			<?php			$cur_page = (int) $this->uri->segment($pagination_config['uri_segment']);			if($cur_page != 0){				$item_per_page = $pagination_config['per_page'];				$no = (($cur_page -1) * $item_per_page) + 1;			}else{				$no = 1;			}			?>			<?php foreach ($data['entries'] as $data_entry): ?>			<tr>				<td><?php echo $no; $no++; ?></td>				<td><?php echo $data_entry['nama']; ?></td>				<td><?php echo $status[$data_entry['status']]; ?></td>				<td><ul><?php				$form = json_decode($data_entry['form'],true);				echo '<li>Type Polis : '.ucfirst($form['pemilik']).'</li>';				$tanggung = (isset($form['tanggung'])) ? "Tidak" : 'Ya' ;				echo '<li>Polis = Tertanggung : '.$tanggung.'</li>';				?></ul></td>				<?php if($data_entry['created_on']){ ?>				<td><?php echo format_date($data_entry['created_on'], 'd-m-Y G:i'); ?></td>				<?php }else{ ?>				<td>-</td>				<?php } ?>				<td class="actions">				<?php				if(group_has_role('daftar', 'view_all_data')){					echo anchor('admin/daftar/data/view/' . $data_entry['id'], '<i class="fa fa-eye"></i> '.lang('global:view'), 'class="btn blue"');				}elseif(group_has_role('daftar', 'view_own_data')){					if($data_entry['created_by']['user_id'] == $this->current_user->id){						echo anchor('admin/daftar/data/view/' . $data_entry['id'], '<i class="fa fa-eye"></i> '.lang('global:view'), 'class="btn blue"');					}				}				?>				<?php				if(group_has_role('daftar', 'edit_all_data')){					echo anchor('admin/daftar/data/edit/' . $data_entry['id'], '<i class="fa fa-pencil"></i> '.lang('global:edit'), 'class="btn green"');				}elseif(group_has_role('daftar', 'edit_own_data')){					if($data_entry['created_by']['user_id'] == $this->current_user->id){						echo anchor('admin/daftar/data/edit/' . $data_entry['id'], '<i class="fa fa-pencil"></i> '.lang('global:edit'), 'class="btn green"');					}				}				?>				<?php				if(group_has_role('daftar', 'delete_all_data')){					echo anchor('admin/daftar/data/delete/' . $data_entry['id'], '<i class="fa fa-trash-o"></i> '.lang('global:delete'), array('class' => 'confirm btn red'));				}elseif(group_has_role('daftar', 'delete_own_data')){					if($data_entry['created_by']['user_id'] == $this->current_user->id){						echo anchor('admin/daftar/data/delete/' . $data_entry['id'], '<i class="fa fa-trash-o"></i> '.lang('global:delete'), array('class' => 'confirm btn red'));					}				}				?>				</td>			</tr>			<?php endforeach; ?>		</tbody>	</table>	<?php echo $data['pagination']; ?><?php else: ?>	<div class="well"><?php echo lang('daftar:data:no_entry'); ?></div><?php endif;?><script type="text/javascript">	$(document).ready(function() {		$('#filterform').on('click', '.btn-reset', function(event) {			event.preventDefault();			location.href = location.protocol + '//' + location.host + location.pathname;		});	});</script>