<div class="page-header">	<h1><?php echo lang('location:kecamatan:plural'); ?></h1>		<div class="btn-group content-toolbar">		<a class="btn btn-yellow btn-sm" href="<?php echo site_url('admin/location/kecamatan/create' . '?' . $_SERVER['QUERY_STRING']); ?>">			<i class="icon-plus"></i>			<span class="no-text-shadow"><?php echo lang('location:kecamatan:new'); ?></span>		</a>	</div></div><?php echo form_open('', array('class' => 'form-inline', 'method' => 'get'), array('f_module' => $module_details['slug'])) ?>	<div class="form-group">		<label><?php echo lang('location:nama_provinsi'); ?>:&nbsp;</label>		<?php 			$value = NULL;			if($this->input->get('f-provinsi')){				$value = $this->input->get('f-provinsi');			}		?>		<select name="f-provinsi" class="" id="provinsi" >			<option value="">-- Pilih Provinsi --</option>			<?php foreach ($provinsi as $prov) { ?>				<option value="<?php echo $prov['id']; ?>" <?php echo ($value==$prov['id']) ? 'selected' : ''; ?>><?php echo $prov['nama']; ?></option>			<?php } ?>		</select>	</div>	<div class="form-group">		<label><?php echo lang('location:nama_kota'); ?>:&nbsp;</label>		<?php 			$value = NULL;			if($this->input->get('f-kota')){				$value = $this->input->get('f-kota');			}		?>		<select name="f-kota" class="" id="kota" >			<option value="">-- Pilih Kota --</option>			<?php foreach ($kota as $kota) { ?>				<option value="<?php echo $kota['id']; ?>" <?php echo ($value==$kota['id']) ? 'selected' : ''; ?>><?php echo $kota['nama']; ?></option>			<?php } ?>		</select>	</div>	<div class="form-group">		<button href="<?php echo current_url() . '#'; ?>" class="btn btn-primary btn-xs" type="submit">			<i class="icon-filter"></i>			<?php echo lang('global:filters'); ?>		</button>				<button href="<?php echo current_url() . '#'; ?>" class="btn btn-xs" type="reset">			<i class="icon-remove"></i>			<?php echo lang('buttons:clear'); ?>		</button>	</div><?php echo form_close() ?><?php if ($kecamatan['total'] > 0): ?>		<p class="pull-right"><?php echo lang('location:showing').' '.count($kecamatan['entries']).' '.lang('location:of').' '.$kecamatan['total'] ?></p>		<table class="table table-striped table-bordered table-hover">		<thead>			<tr>				<th>No</th>				<th><?php echo lang('location:nama_kecamatan'); ?></th>				<th><?php echo lang('location:nama_kota'); ?></th>				<th><?php echo lang('location:nama_provinsi'); ?></th>				<th></th>			</tr>		</thead>		<tbody>			<?php 			$cur_page = (int) $this->uri->segment($pagination_config['uri_segment']);			if($cur_page != 0){				$no = $cur_page + 1;			}else{				$no = 1;			}			?>						<?php foreach ($kecamatan['entries'] as $kecamatan_entry): ?>			<tr>				<td><?php echo $no; $no++; ?></td>				<td>				<?php 				echo $kecamatan_entry['nama']; 				echo '&nbsp;&nbsp;';				echo anchor('admin/location/kelurahan/index?f-provinsi=' . $kecamatan_entry['id_provinsi'] . '&f-kota=' . $kecamatan_entry['id_kota'] . '&f-kecamatan=' . $kecamatan_entry['id'], 'Daftar Kelurahan', '');				?>				</td>				<td><?php echo $kecamatan_entry['nama_kota']; ?></td>				<td><?php echo $kecamatan_entry['nama_provinsi']; ?></td>							<td class="actions">				<?php 				echo anchor('admin/location/kecamatan/edit/' . $kecamatan_entry['id'] . '?' . $_SERVER['QUERY_STRING'], lang('global:edit'), 'class="btn btn-xs btn-info edit"');									echo anchor('admin/location/kecamatan/delete/' . $kecamatan_entry['id'] . '?' . $_SERVER['QUERY_STRING'], lang('global:delete'), array('class' => 'confirm btn btn-xs btn-danger delete'));				?>				</td>			</tr>			<?php endforeach; ?>		</tbody>	</table>		<?php echo $kecamatan['pagination']; ?>	<?php else: ?>	<div class="well"><?php echo lang('location:kecamatan:no_entry'); ?></div><?php endif;?><script type="text/javascript">	$(document).ready(function() {		$('#provinsi').change(function() {			var id_provinsi = $(this).val();			$('#kota option:first').html('-- Sedang Mengambil Data --');			$.getJSON(BASE_URL+'admin/location/kecamatan/get_kota_by_provinsi/'+id_provinsi, function(result) {				var choose = '-- Pilih Kota --';				if(result.length==0) {					choose = '-- Tidak Ada Kota --'				}				$('#kota').html('<option value="">'+choose+'</option>');				$.each(result, function(key,value) {					$('#kota').append('<option value="'+value.id+'">'+value.nama+'</option>');				});			});		});		$('#kota').change(function() {			var id_kota = $(this).val();			$('#kecamatan option:first').html('-- Sedang Mengambil Data --');			$.getJSON(BASE_URL+'admin/location/kelurahan/get_kecamatan_by_kota/'+id_kota, function(result) {				var choose = '-- Pilih Kecamatan --';				if(result.length==0) {					choose = '-- Tidak Ada Kecamatan --'				}				$('#kecamatan').html('<option value="">'+choose+'</option>');				$.each(result, function(key,value) {					$('#kecamatan').append('<option value="'+value.id+'">'+value.nama+'</option>');				});			});		});	});</script>