<div class="page-header">	<h1><?php echo lang('location:kota:plural'); ?></h1>		<div class="btn-group content-toolbar">		<a class="btn btn-yellow btn-sm" href="<?php echo site_url('admin/location/kota/create' . '?' . $_SERVER['QUERY_STRING']); ?>">			<i class="icon-plus"></i>			<span class="no-text-shadow"><?php echo lang('location:kota:new'); ?></span>		</a>	</div></div><?php echo form_open('', array('class' => 'form-inline', 'method' => 'get'), array('f_module' => $module_details['slug'])) ?>	<div class="form-group">		<label><?php echo lang('location:nama_provinsi'); ?>:&nbsp;</label>		<?php 			$value = NULL;			if($this->input->get('f-provinsi') != NULL){				$value = $this->input->get('f-provinsi');			}		?>		<select name="f-provinsi" class="" id="provinsi" >			<option value="">-- Pilih Provinsi --</option>			<?php foreach ($provinsi as $prov) { ?>				<option value="<?php echo $prov['id']; ?>" <?php echo ($value==$prov['id']) ? 'selected' : ''; ?>><?php echo $prov['nama']; ?></option>			<?php } ?>		</select>	</div>	<div class="form-group">		<button href="<?php echo current_url() . '#'; ?>" class="btn btn-primary btn-xs" type="submit">			<i class="icon-filter"></i>			<?php echo lang('global:filters'); ?>		</button>				<button href="<?php echo current_url() . '#'; ?>" class="btn btn-xs" type="reset">			<i class="icon-remove"></i>			<?php echo lang('buttons:clear'); ?>		</button>	</div><?php echo form_close() ?><?php if ($kota['total'] > 0): ?>		<p class="pull-right"><?php echo lang('location:showing').' '.count($kota['entries']).' '.lang('location:of').' '.$kota['total'] ?></p>		<table class="table table-striped table-bordered table-hover">		<thead>			<tr>				<th>No</th>				<th><?php echo lang('location:nama_kota'); ?></th>				<th><?php echo lang('location:nama_provinsi'); ?></th>				<th></th>			</tr>		</thead>		<tbody>			<?php 			$cur_page = (int) $this->uri->segment($pagination_config['uri_segment']);			if($cur_page != 0){				$no = $cur_page + 1;			}else{				$no = 1;			}			?>						<?php foreach ($kota['entries'] as $kota_entry): ?>			<tr>				<td><?php echo $no; $no++; ?></td>				<td>				<?php 				echo $kota_entry['nama']; 				echo '&nbsp;&nbsp;';				echo anchor('admin/location/kecamatan/index?f-provinsi=' . $kota_entry['id_provinsi'] . '&f-kota=' . $kota_entry['id'], 'Daftar Kecamatan', '');				?>				</td>				<td><?php echo $kota_entry['nama_provinsi']; ?></td>							<td class="actions">				<?php 				echo anchor('admin/location/kota/edit/' . $kota_entry['id'] . '?' . $_SERVER['QUERY_STRING'], lang('global:edit'), 'class="btn btn-xs btn-info edit"');								echo anchor('admin/location/kota/delete/' . $kota_entry['id'] . '?' . $_SERVER['QUERY_STRING'], lang('global:delete'), array('class' => 'confirm btn btn-xs btn-danger delete'));				?>				</td>			</tr>			<?php endforeach; ?>		</tbody>	</table>		<?php echo $kota['pagination']; ?>	<?php else: ?>	<div class="well"><?php echo lang('location:kota:no_entry'); ?></div><?php endif;?>