<div class="page-header">	<h1><?php echo lang('location:provinsi:plural'); ?></h1>		<div class="btn-group content-toolbar">		<a href="<?php echo base_url('admin/location/provinsi/create'); ?>" class="btn btn-yellow btn-sm"><i class="icon-plus"></i><span class="no-text-shadow"><?php echo lang('location:provinsi:new'); ?></span></a>	</div></div><?php if ($provinsi['total'] > 0): ?>		<p class="pull-right"><?php echo lang('location:showing').' '.count($provinsi['entries']).' '.lang('location:of').' '.$provinsi['total'] ?></p>		<table class="table table-striped table-bordered table-hover">		<thead>			<tr>				<th style="width:50px">No</th>				<th><?php echo lang('location:nama_provinsi'); ?></th>				<th style="width:200px"></th>			</tr>		</thead>		<tbody>			<?php 			$no = 1;						?>						<?php foreach ($provinsi['entries'] as $provinsi_entry): ?>			<tr>				<td><?php echo $no; $no++; ?></td>				<td>				<?php 				echo $provinsi_entry['nama']; 				echo '&nbsp;&nbsp;';				echo anchor('admin/location/kota/index?f-provinsi=' . $provinsi_entry['id'], 'Daftar Kota', '');				?>				</td>				<td class="actions">				<?php 				echo ($provinsi_entry['ordering_count']!=1) ? anchor('admin/location/provinsi/move/up/'.$provinsi_entry['id'].'/'.$provinsi_entry['ordering_count'], '<i class="fa fa-angle-up"></i>', 'class="btn btn-xs btn-success up"').'&nbsp;' : '';								echo ($provinsi_entry['ordering_count']!=$provinsi['total']) ? anchor('admin/location/provinsi/move/down/'.$provinsi_entry['id'].'/'.$provinsi_entry['ordering_count'], '<i class="fa fa-angle-down"></i>', 'class="btn btn-xs btn-success down"').'&nbsp;' : '';								echo anchor('admin/location/provinsi/edit/' . $provinsi_entry['id'], lang('global:edit'), 'class="btn btn-xs btn-info edit"').'&nbsp;';								echo anchor('admin/location/provinsi/delete/' . $provinsi_entry['id'], lang('global:delete'), array('class' => 'confirm btn btn-xs btn-danger delete'));								?>				</td>			</tr>			<?php endforeach; ?>		</tbody>	</table>		<?php echo $provinsi['pagination']; ?>	<?php else: ?>	<div class="well"><?php echo lang('location:provinsi:no_entry'); ?></div><?php endif;?>